#!/usr/bin/env python3

from sys import argv
from intcode import Intcode


# CONSTANTS
EMPTY, WALL, BLOCK, HORZ, BALL = 0, 1, 2, 3, 4
EMPTY_CHR, WALL_CHR, BLOCK_CHR, HORZ_CHR, BALL_CHR = ' ', '#', '%', 'â€”', 'O'
block_map = {
    EMPTY: EMPTY_CHR,
    WALL : WALL_CHR,
    BLOCK: BLOCK_CHR,
    HORZ: HORZ_CHR,
    BALL: BALL_CHR
}
LEFT, NEUTRAL, RIGHT = -1, 0, 1
LEFT_INPUT, NEUTRAL_INPUT, RIGHT_INPUT = 'f', '', 'g'
input_map = {
    LEFT_INPUT: LEFT,
    NEUTRAL_INPUT: NEUTRAL,
    RIGHT_INPUT: RIGHT
}
SCREEN_SPAN = [40, 26]

DEBUG = False


def debug(s):
    if DEBUG: print(s)


def get_tiles(flat_list):
    it_list = iter(flat_list)
    for i in it_list:
        yield(i, next(it_list), next(it_list))


def part1(code):
    ic = Intcode(code)
    outputs = []
    for o in ic:
        if o:
            outputs.extend(o)
    tiles = get_tiles(o)
    num_blocks = 0
    for tile in tiles:
        if tile[2] == BLOCK:
            num_blocks += 1
    return num_blocks


def get_screen_dimensions(code):
    ic = Intcode(code)
    outputs = []
    for o in ic:
        if o:
            outputs.extend(o)
    tiles = get_tiles(o)
    first_tile = next(tiles)
    span = [first_tile[0], first_tile[1]]
    for tile in tiles:
        if tile[0] > span[0]:
            span[0] = tile[0]
        if tile[1] > span[1]:
            span[1] = tile[1]
    span = [span[0] + 1, span[1] + 1]
    return span


def draw_game(tile_map):
    for y in range(SCREEN_SPAN[1]):
        for x in range(SCREEN_SPAN[0]):
            block = tile_map.get((x, y), EMPTY)
            print(block_map[block], end='')
        print()


def read_input():
    i = None
    while i not in input_map.keys():
        i = input("Le[f]t | Ri[g]ht | [Nothing] > ")
    return input_map[i]


def get_score(tile_map):
    return tile_map.get((-1, 0), None)


def part2(code):
    code[0] = 2
    ic = Intcode(code)
    o = next(ic)
    tile_map = {(x, y) : tile_type for x, y, tile_type in get_tiles(o)}
    draw_game(tile_map)

    replay_inputs = []  # set this to example winning moves (below) to get answer tpo part 2

    for i in replay_inputs:
        o = ic.send([i])
        tile_map_updates = {(x, y) : tile_type for x, y, tile_type in get_tiles(o)}
        tile_map.update(tile_map_updates)
    draw_game(tile_map)

    while True:
        input = read_input()
        replay_inputs.append(input)
        try:
            o = ic.send([input])
        except StopIteration:
            break
        debug(o)
        tile_map_updates = {(x, y) : tile_type for x, y, tile_type in get_tiles(o)}
        tile_map.update(tile_map_updates)
        draw_game(tile_map)
    score = get_score(tile_map)
    debug(replay_inputs)
    return score


if __name__ == '__main__':
    fn = argv[1] if len(argv) >= 2 else 'input.txt'
    with open(fn, 'r') as f:
        code = [int(x) for x in f.read().split(',')]
    code.extend([0] * 1024)

    res = part1(code.copy())
    print(f'Part 1: {res}')

    res = get_screen_dimensions(code.copy())
    print(f'Screen dimensions: {res}')
    assert res == SCREEN_SPAN

    res = part2(code.copy())
